import React from 'react';
import './Modal.css'; // 모달 스타일

const Modal = ({ isOpen, onClose, onSubmit, categoryData }) => {
  const [title, setTitle] = React.useState(categoryData ? categoryData.title : '');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ title });
    setTitle('');
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{categoryData ? 'Update Category' : 'Add Category'}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Category Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <button type="submit">{categoryData ? 'Update' : 'Add'}</button>
          <button type="button" onClick={onClose}>Close</button>
        </form>
      </div>
    </div>
  );
};

export default Modal;
