import React, { useEffect, useState } from 'react';
import Header from './Header';
import Modal from './Modal'; // 모달 컴포넌트 추가
import "./Product.css"

function ProductList() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ title: '', price: '', description: '', image: '', category: '' });
  const [modalOpen, setModalOpen] = useState(false);

  // OPEN API
  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(json => setProducts(json))
      .catch(error => console.error("Error fetching data:", error));

    // 카테고리 가져오기
    fetch('https://fakestoreapi.com/products/categories')
      .then(res => res.json())
      .then(json => setCategories(json))
      .catch(error => console.error("Error fetching categories:", error));
  }, []);

  useEffect(() => {
    const filtered = selectedCategory === 'all'
      ? products
      : products.filter(product => product.category === selectedCategory);
    setFilteredProducts(filtered);
  }, [selectedCategory, products]);

  const addProduct = () => {
    fetch('https://fakestoreapi.com/products', {
      method: "POST",
      body: JSON.stringify(newProduct),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .then(json => {
        setProducts([...products, json]);
        setNewProduct({ title: '', price: '', description: '', image: '', category: '' }); // 초기화
        setModalOpen(false); // 모달 닫기
      })
      .catch(error => console.error("Error adding product:", error));
  };

  const openModal = () => {
    setModalOpen(true);
  };

  return (
    <div>
      <div className="container-fluid header mb-3">
        <Header />
      </div>
      <div className="container">
        <h1 className="product-list-title">Product List</h1>

        {/* 카테고리 선택 드롭다운 */}
        <div className="button-container">
        <select 
          className="select-category"
          value={selectedCategory} 
          onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="all">All Categories</option>
          {categories.map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>

        {/* 새 상품 추가 버튼 */}
        <button className="add-product-button" onClick={openModal}>Add Product</button>
      </div>

        <ul className="product-list">
          {filteredProducts.map(product => (
            <li key={product.id} className="product-item">
              <img src={product.image} alt={product.title} className="product-image" />
              <h2 className="product-title">{product.title}</h2>
              <p className="product-description">{product.description.substring(0, 100)}...</p>
              <p><strong className="product-price">${product.price}</strong></p>
            </li>
          ))}
        </ul>
      </div>

      {/* 상품 추가 모달 컴포넌트 */}
      <Modal 
        isOpen={modalOpen} 
        onClose={() => setModalOpen(false)} 
        onSubmit={addProduct} 
        newProduct={newProduct} // 현재 상품 데이터 전달
        setNewProduct={setNewProduct} // 상품 데이터 업데이트 함수 전달
      />
    </div>
  );
}

export default ProductList;
