import React, { useEffect, useState } from 'react';
import Header from './Header';
import Modal from './Modal'; // 모달 컴포넌트 추가
import "./Product.css"

function ProductList() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ title: '', price: '', description: '', image: '', category: '' });
  const [modalOpen, setModalOpen] = useState(false);
  const [currentCategory, setCurrentCategory] = useState(null); // 현재 카테고리 업데이트를 위한 상태

  // OPEN API
  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(json => setProducts(json))
      .catch(error => console.error("Error fetching data:", error));

    // 카테고리 가져오기
    fetch('https://fakestoreapi.com/products/categories')
      .then(res => res.json())
      .then(json => setCategories(json))
      .catch(error => console.error("Error fetching categories:", error));
  }, []);

  useEffect(() => {
    const filtered = selectedCategory === 'all'
      ? products
      : products.filter(product => product.category === selectedCategory);
    setFilteredProducts(filtered);
  }, [selectedCategory, products]);

  const addProduct = () => {
    fetch('https://fakestoreapi.com/products', {
      method: "POST",
      body: JSON.stringify(newProduct),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .then(json => {
        setProducts([...products, json]);
        setNewProduct({ title: '', price: '', description: '', image: '', category: '' }); // 초기화
      })
      .catch(error => console.error("Error adding product:", error));
  };

  const openModal = (category = null) => {
    setCurrentCategory(category);
    setModalOpen(true);
  };

  const handleModalSubmit = (data) => {
    if (currentCategory) {
      // 카테고리 업데이트
      fetch(`https://fakestoreapi.com/products/category/${currentCategory}`, {
        method: "PUT",
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then(res => res.json())
        .then(json => {
          // 카테고리 업데이트 후 상태 변경
          const updatedCategories = categories.map(cat =>
            cat === currentCategory ? json : cat
          );
          setCategories(updatedCategories);
        })
        .catch(error => console.error("Error updating category:", error));
    } else {
      // 카테고리 추가
      fetch('https://fakestoreapi.com/products/categories', {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then(res => res.json())
        .then(json => {
          setCategories([...categories, json]);
        })
        .catch(error => console.error("Error adding category:", error));
    }
  };

  return (
    <div>
      <div className="container-fluid header mb-3">
        <Header />
      </div>
      <div className="container">
        <h1 className="product-list-title">Product List</h1>

        {/* 카테고리 선택 드롭다운 */}
        <select 
          className="select-category"
          value={selectedCategory} 
          onChange={(e) => setSelectedCategory(e.target.value)} 
          style={{ marginBottom: '1rem' }}>
          <option value="all">All Categories</option>
          {categories.map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>

        {/* 새 카테고리 추가 버튼 */}
        <button onClick={() => openModal()}>Add Category</button>
        <button onClick={() => openModal(currentCategory)}>Update Category</button>

        {/* 새 제품 추가 폼 */}
        <h2 className="add-product-title">Add New Product</h2>
        <div className="add-product-form">
          <input 
            className="product-input"
            type="text" 
            placeholder="Title" 
            value={newProduct.title}
            onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })} 
          />
          <input 
            className="product-input"
            type="number" 
            placeholder="Price" 
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Description" 
            value={newProduct.description}
            onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Image URL" 
            value={newProduct.image}
            onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Category" 
            value={newProduct.category}
            onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })} 
          />
          <button className="add-product-button" onClick={addProduct}>Add Product</button>
        </div>

        <ul className="product-list">
          {filteredProducts.map(product => (
            <li key={product.id} className="product-item">
              <img src={product.image} alt={product.title} className="product-image" />
              <h2 className="product-title">{product.title}</h2>
              <p className="product-description">{product.description.substring(0, 100)}...</p>
              <p><strong className="product-price">${product.price}</strong></p>
              <button className="update-button" onClick={() => updateProduct(product.id)}>Update</button>
              <button className="delete-button" onClick={() => deleteProduct(product.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>

      {/* 모달 컴포넌트 */}
      <Modal 
        isOpen={modalOpen} 
        onClose={() => setModalOpen(false)} 
        onSubmit={handleModalSubmit} 
        categoryData={currentCategory} // 현재 카테고리 데이터 전달
      />
    </div>
  );
}

export default ProductList;
