import React, { useEffect, useState } from 'react';
import Header from './Header';
import './Product.css';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');

  // 새 제품을 추가하기 위한 상태
  const [newProduct, setNewProduct] = useState({
    title: '',
    price: '',
    description: '',
    image: '',
    category: ''
  });

  useEffect(() => {
    // 전체 제품 데이터 가져오기
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(json => {
        setProducts(json);
        console.log("All Products:", json); // 전체 제품 출력
      })
      .catch(error => console.error("Error fetching products:", error));

    // 카테고리 데이터 가져오기
    fetch('https://fakestoreapi.com/products/categories')
      .then(res => res.json())
      .then(json => {
        setCategories(json);
        console.log("Categories:", json); // 카테고리 출력
      })
      .catch(error => console.error("Error fetching categories:", error));
  }, []);

  // 선택된 카테고리에 따라 제품 필터링
  const filteredProducts = selectedCategory === 'all'
    ? products
    : products.filter(product => product.category === selectedCategory);

  // 새 제품 추가 함수
  const addProduct = () => {
    fetch('https://fakestoreapi.com/products', {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newProduct)
    })
    .then(res => res.json())
    .then(json => {
      console.log("Added Product:", json);
      setProducts(prev => [...prev, json]); // 제품 목록에 추가
      setNewProduct({ title: '', price: '', description: '', image: '', category: '' }); // 입력 필드 초기화
    })
    .catch(error => console.error("Error adding product:", error));
  };

  // 제품 업데이트 함수
  const updateProduct = (id) => {
    fetch(`https://fakestoreapi.com/products/${id}`, {
      method: "PUT",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newProduct)
    })
    .then(res => res.json())
    .then(json => {
      console.log("Updated Product:", json);
      setProducts(prev => prev.map(product => product.id === id ? json : product)); // 목록에서 업데이트
    })
    .catch(error => console.error("Error updating product:", error));
  };

  // 제품 삭제 함수
  const deleteProduct = (id) => {
    fetch(`https://fakestoreapi.com/products/${id}`, {
      method: "DELETE"
    })
    .then(res => res.json())
    .then(json => {
      console.log("Deleted Product:", json);
      setProducts(prev => prev.filter(product => product.id !== id)); // 목록에서 삭제
    })
    .catch(error => console.error("Error deleting product:", error));
  };
  return (
    <div>
      <div className="container-fluid header mb-3">
        <Header />
      </div>
      <div className="container">
        <h1 className="product-list-title">Product List</h1>

        {/* 카테고리 선택 드롭다운 */}
        <select 
          className="select-category"
          value={selectedCategory} 
          onChange={(e) => setSelectedCategory(e.target.value)} 
          style={{ marginBottom: '1rem' }}>
          <option value="all">All Categories</option>
          {categories.map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>

        {/* 새 제품 추가 폼 */}
        <h2 className="add-product-title">Add New Product</h2>
        <div className="add-product-form">
          <input 
            className="product-input"
            type="text" 
            placeholder="Title" 
            value={newProduct.title}
            onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })} 
          />
          <input 
            className="product-input"
            type="number" 
            placeholder="Price" 
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Description" 
            value={newProduct.description}
            onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Image URL" 
            value={newProduct.image}
            onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })} 
          />
          <input 
            className="product-input"
            type="text" 
            placeholder="Category" 
            value={newProduct.category}
            onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })} 
          />
          <button className="add-product-button" onClick={addProduct}>Add Product</button>
        </div>

        <ul className="product-list">
          {filteredProducts.map(product => (
            <li key={product.id} className="product-item">
              <img src={product.image} alt={product.title} className="product-image" />
              <h2 className="product-title">{product.title}</h2>
              <p className="product-description">{product.description.substring(0, 100)}...</p>
              <p><strong className="product-price">${product.price}</strong></p>
              <button className="update-button" onClick={() => updateProduct(product.id)}>Update</button>
              <button className="delete-button" onClick={() => deleteProduct(product.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );


}

export default ProductList;
