import React, { Component } from "react";
import data from "./utils/db"; // 데이터 가져오기
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from "./components/Header"; // 헤더 컴포넌트
import ProductList from "./components/ProductList"; // 제품 리스트 컴포넌트
import About from "./components/About"; // 어바웃 페이지 컴포넌트

class App extends Component {
  state = {
    stocks: data, // 초기 데이터를 stocks에 저장
    category: this.getCategory(data), // 카테고리 초기화
  };

  getCategory = (data) => {
    const category = data.map((e) => e.category);
    return Array.from(new Set(category)); // 중복 카테고리 제거
  };

  handleSearch = (filteredData) => {
    // 검색 기능 구현
  };

  render() {
    return (
      <Router>
        <Header onSearch={this.handleSearch} />
        <Routes>
          <Route path="/" element={<ProductList stocks={this.state.stocks} category={this.state.category} />} />
          <Route path="/about" element={<About />} />
        </Routes>
      </Router>
    );
  }
}

export default App;
